class StationInfo {
  final int position;
  final double xcoord;
  final double ycoord;
  final String name;
  final String iconImage;
  final String description;
  final String fulltext;
  final String video;
  final int next;

  final String zusatzvideo;
  final String zusatztext;

  StationInfo(
    this.position, {
    this.xcoord = 0,
    this.ycoord = 0,
    this.name = "",
    this.iconImage = "",
    this.description = "",
    this.fulltext = "",
    this.video = "",
    this.next = 0,
    this.zusatzvideo = "",
    this.zusatztext = "",
  });
}

